mapper.select:
SELECT B.BBS_SN, B.BBS_CTGRY_NO, CT.CTGRY_NM AS BBS_CTGRY_NM, B.EMPL_NO, E.EMPL_NM, B.BBSCTT_SJ, B.BBSCTT_CN, B.BBSCTT_CREAT_DT, B.BBSCTT_UPDT_DT, B.RDCNT, B.BBSCTT_USE_YN, B.UPEND_FIXING_YN, B.ATCH_FILE_NO,
-- üëç Ï¢ãÏïÑÏöî Ïàò (SELECT COUNT(*) FROM BBS_LIKE L WHERE L.BBS_SN = B.BBS_SN AND L.BBS_CTGRY_NO = B.BBS_CTGRY_NO) AS LIKE_CNT
FROM BBS B JOIN EMPLOYEE E ON B.EMPL_NO = E.EMPL_NO JOIN BBS_CTGRY CT ON B.BBS_CTGRY_NO = CT.BBS_CTGRY_NO WHERE B.BBS_SN = #{bbsSn}

mapper.update:
UPDATE BBS
SET RDCNT = NVL(RDCNT, 0) + 1
WHERE BBS_SN = #{bbsSn}
